namespace AppBundle\Entity\BelysningTiltagDetail;

use Doctrine\ORM\EntityRepository;

/**
 * ErstatningsLyskildeRepository.
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ErstatningsLyskildeRepository extends EntityRepository {
  /**
   * Check if entity can be removed (deleted). If not, return an error message.
   *
   * @return string|null
   */
  public function getRemoveErrorMessage(ErstatningsLyskilde $erstatningsLyskilde) {
    $query = $this->_em->createQuery('SELECT d FROM AppBundle:BelysningTiltagDetail d WHERE d.erstatningsLyskilde = :erstatningsLyskilde');
    $query->setParameter('erstatningsLyskilde', $erstatningsLyskilde);
    $result = $query->getResult();

    if ($result) {
      return 'erstatningslyskilde.error.in_use';
    }

    return null;
  }

  /**
   * Helper function to convert values to proper type.
   *
   * @param string $column
   * @param string $value
   *
   * @return string
   *
   * @throws
   */
  public function getTypedValue($column, $value) {
    switch ($column) {
      default;
        // Most of values are stringable.
        return (string) $value;
    }
  }

}
